// Parse extracted products and prepare for database
const aiResponse = $input.first().json.choices[0].message.content;
const originalCompany = $input.first().json;

try {
  // Clean and parse JSON response
  let cleanJson = aiResponse.replace(/```json\s*|```/g, '').trim();
  const products = JSON.parse(cleanJson);

  if (!Array.isArray(products) || products.length === 0) {
    return [{
      json: {
        skipped: true,
        reason: 'No products found',
        company: originalCompany.name,
        website: originalCompany.website
      }
    }];
  }

  // Process each product
  return products.map((product, index) => {
    return {
      json: {
        // Core product data for database
        name: product.name || 'Unknown Product',
        description: product.description || '',
        price: typeof product.price === 'number' ? product.price : 0,
        category: product.category || originalCompany.category,
        imageUrl: product.imageUrl || '',
        externalUrl: product.externalUrl || originalCompany.website,
        hasTrial: Boolean(product.hasTrial),
        detailInfo: product.detailInfo || '',

        // JSON fields - properly formatted
        tags: JSON.stringify(product.tags || []),
        advantages: JSON.stringify(product.advantages || []),
        disadvantages: JSON.stringify(product.disadvantages || []),
        pricingInfo: JSON.stringify(product.pricingInfo || {}),
        videoUrls: JSON.stringify([]),

        // Metadata
        sourceCompany: originalCompany.name,
        sourceWebsite: originalCompany.website,
        extractedAt: new Date().toISOString(),
        productIndex: index + 1
      }
    };
  });

} catch (error) {
  console.error('Error processing products:', error);
  return [{
    json: {
      error: `Failed to process products: ${error.message}`,
      company: originalCompany.name,
      website: originalCompany.website,
      aiResponse: aiResponse.substring(0, 500)
    }
  }];
}

// Check for duplicate products in database
const newProduct = $input.first().json;
const existingProductsResponse = $input.last().json;

// Get existing products array
const existingProducts = existingProductsResponse.products || [];

// Check for duplicates by name (case insensitive)
const productExists = existingProducts.some(existing => 
  existing.name.toLowerCase().trim() === newProduct.name.toLowerCase().trim()
);

if (productExists) {
  return [{
    json: {
      isDuplicate: true,
      productName: newProduct.name,
      reason: 'Product with same name already exists',
      skipped: true
    }
  }];
} else {
  return [{
    json: {
      ...newProduct,
      isDuplicate: false,
      readyForInsert: true
    }
  }];
}

// Generate final workflow summary
const items = $input.all();

let successCount = 0;
let skippedCount = 0;
let errorCount = 0;

const successProducts = [];
const skippedProducts = [];
const errorDetails = [];

items.forEach(item => {
  const result = item.json.result;

  if (result === 'success') {
    successCount++;
    successProducts.push({
      name: item.json.productName,
      id: item.json.productId
    });
  } else if (result === 'skipped') {
    skippedCount++;
    skippedProducts.push({
      name: item.json.productName,
      reason: item.json.reason
    });
  } else {
    errorCount++;
    errorDetails.push({
      company: item.json.company,
      website: item.json.website,
      reason: item.json.reason
    });
  }
});

const summary = {
  totalProcessed: items.length,
  successfulInserts: successCount,
  skippedDuplicates: skippedCount,
  errors: errorCount,
  timestamp: new Date().toISOString(),
  successProducts,
  skippedProducts,
  errorDetails
};

console.log('WORKFLOW SUMMARY:', JSON.stringify(summary, null, 2));

return [{ json: summary }];
